        </ul>

        <div class="dropdown-custom">
          <!-- Ícone do perfil -->
          <div class="dropdown-icon" @click="toggleDropdown">
            <img src="@/assets/icons/userProfile.svg" alt="User Icon" class="icon" />

          </div>

          <!-- Opções do dropdown -->
          <div v-if="isDropdownOpen" class="dropdown-menu-custom">
            <ul>
              <li>
                <router-link to="/perfil" @click="closeDropdown">Profile</router-link>
              </li>
              <li>
                <button @click="logout">Logout</button>
              </li>
            </ul>
          </div>
