<script>
import { mapActions } from "vuex";

export default {
  name: "Header",
  data() {
    return {
      isDropdownOpen: false,
    }
  },

  methods: {
    ...mapActions({
      userLogout: 'Auth/logout',
      login: 'Auth/login'
    }),
    toggleDropdown() {
      this.isDropdownOpen = !this.isDropdownOpen;
    },
    // Fecha o menu ao clicar fora ou em um item do menu
    closeDropdown(event) {
      const dropdown = this.$el.querySelector(".dropdown-custom");

      // Se o clique for dentro do dropdown e não em um link/botão, não fecha
      if (dropdown && dropdown.contains(event.target)) {
        const isOption = event.target.tagName === "A" || event.target.tagName === "BUTTON";
        if (!isOption) return;
      }

      this.isDropdownOpen = false;
    },
    logout() {
      this.userLogout()
          .then(() => {
            setTimeout(function () {
              window.location.reload();
            }, 200)
          }).catch((error) => {
            console.log('Erro no logout:',error)
      })
    }
  },
  mounted() {
    document.addEventListener("click", this.closeDropdown);
  },

  beforeUnmount() {
    document.removeEventListener("click", this.closeDropdown);
  }
}
</script>

